$.ajax({type:"GET",url:"wp-content/themes/countdown/lib/countdown/get-countdown.php",dataType:"json",beforeSend:function(){},success:function(e){i=0;$.each(e,function(e,t){function n(e,t,n,r,i,s){var o=(new Date).getTime(),u=(new Date(due_yr,due_month,due_day,0,0,0)).getTime();days_left=Math.floor((u-o)/864e5),hours_left=Math.floor((u-o)%864e5/36e5),minutes_left=Math.floor((u-o)%864e5%36e5/6e4),seconds_left=Math.floor((u-o)%864e5%36e5%6e4/1e3);return{days:days_left,hours:hours_left,minutes:minutes_left,seconds:seconds_left}}countDownWrapper=$(".countdown-holster");dataTitle=t.project_title;dataColor=t.project_color;dataDeal=t.big_deal;dataDueDate=t.due_date;due_yr=dataDueDate.substring(0,4);due_month=dataDueDate.substring(4,6)-1;due_day=dataDueDate.substring(6);countDownWrapper.append('<div class="large-3 columns end" data-title="'+dataTitle+'" data-color="'+dataColor+'" data-deal="'+dataDeal+'">'+"<h4>"+dataTitle+"</h4>"+"<p>"+days_left+" days</p>"+"<p>"+0/0+"<p>"+null+" minutes</p>"+"<p>"+null+" seconds</p>"+"</div>").children().hide().each(function(){$(this).css("background-color",$(this).data("color"))})})},complete:function(){i=0;countDownWrapper.children().each(function(){var e=$(this);setTimeout(function(){e.fadeIn(800);e.find("p").fitText(.65)},800*i++)})},error:function(){}});