$.ajax({type:"GET",url:"wp-content/themes/countdown/lib/countdown/get-countdown.php",dataType:"json",beforeSend:function(){},success:function(e){var t,n,r,s,o;i=0;$.each(e,function(e,t){function i(e,t,i,u,a,f){var l=(new Date).getTime(),c=(new Date(due_yr,due_month,due_day,0,0,0)).getTime();n=Math.floor((c-l)/864e5),r=Math.floor((c-l)%864e5/36e5),s=Math.floor((c-l)%864e5%36e5/6e4),o=Math.floor((c-l)%864e5%36e5%6e4/1e3);return{days:n,hours:r,minutes:s,seconds:o}}countDownWrapper=$(".countdown-holster");dataTitle=t.project_title;dataColor=t.project_color;dataDeal=t.big_deal;dataDueDate=t.due_date;dataLink=t.link;dataID=t.project_id;due_yr=dataDueDate.substring(0,4);due_month=dataDueDate.substring(4,6)-1;due_day=dataDueDate.substring(6);countDownWrapper.append('<div class="large-3 columns end" data-id="'+dataID+'" data-title="'+dataTitle+'" data-color="'+dataColor+'" data-deal="'+dataDeal+'">'+'<h4><a href="'+dataLink+'">'+dataTitle+"</a></h4>"+"<p><span>"+n+"</span> days</p>"+"<p><span>"+null+"</span> hours</p>"+"<p><span>"+null+"</span> minutes</p>"+"<p><span>"+null+"</span> seconds</p>"+"</div>").children().hide().each(function(){var e=1;$(this).find("span").each(function(){e-=.15;$(this).css({color:$(this).parent().parent().data("color"),opacity:e})})})})},complete:function(){i=0;countDownWrapper.children().each(function(){var e=$(this);setTimeout(function(){e.fadeIn(800);e.find("p").fitText(.65)},800*i++)})},error:function(){}});